svg.script:
 var doc, svg, background, thumb;

 function onLoad(evt) {
 	svg = evt.target;
 	doc = svg.ownerDocument;
 	background = doc.getElementById("background");
 	thumb = doc.getElementById("thumb");
 	background.setAttribute("fill", "#808080");
 	background.setAttribute("opacity", "0.6");
 	thumb.setAttribute("fill", "black");
 	thumb.setAttribute("opacity", "0.6");
 }

 function onResize(evt) {
 	var width = parseInt(svg.getAttribute("width"));
 	var height = parseInt(svg.getAttribute("height"));

	var value = $model.value;

	var w = width - 4;
	var h = Math.round(height / 5);
	var dy = Math.round((height - h) * value / 100);

	background.setAttribute("width", w);
	background.setAttribute("height", height);
	thumb.setAttribute("y", dy);
	thumb.setAttribute("width", w);
	thumb.setAttribute("height", h);
 }
